NexT.plugins.others.math=function(){const a=NexT.CONFIG.page.math.render;if(a==='mathjax'){const a=NexT.utils.getCDNResource(NexT.CONFIG.page.math.js);NexT.utils.getScript(a,function(){window.MathJax={tex:{inlineMath:[["$","$"]]}}})}if(a==='katex'){const a=NexT.utils.getCDNResource(NexT.CONFIG.page.math.css);NexT.utils.getStyle(a);const b=NexT.CONFIG.page.math.js;b.forEach(a=>{const b=NexT.utils.getScript(NexT.utils.getCDNResource(a));a.name==='auto-render'&&b.then(function(){renderMathInElement(document.body,{delimiters:[{left:'$$',right:'$$',display:!0},{left:'$',right:'$',display:!1},{left:'\\(',right:'\\)',display:!1},{left:'\\[',right:'\\]',display:!0}],throwOnError:!1})})})}}